INSERT INTO `message_reply` VALUES (13,6,'靓靓','杨开','2013-02-18 01:05:02','谢谢你的祝福，也祝你新年快乐，合家幸福。'),(18,4,'杨开','薰衣草','2013-02-18 02:44:17','thanks'),(23,2,'杨开','靓靓','2013-04-29 16:31:55','fuck'),(24,10,'杨开','生如夏花','2013-05-16 20:39:35','还没有确定。'),(25,9,'杨开','靓靓','2013-05-16 20:40:03','没有实习，毕业后直接去上班。');
